var menu=document.querySelector(".nav__list"),burger=document.querySelector(".burger"),doc=$(document),l=$(".scrolly"),panel=$(".panel"),vh=$(window).height(),openMenu=function(){burger.classList.toggle("burger--active"),menu.classList.toggle("nav__list--active")},scrollFx=function(){for(var e=doc.scrollTop(),n=vh/4,o=0;o<panel.length;o++)panel.eq(o).offset().top<e+n?panel.eq(o).find(".panel__content").addClass("panel__content--active"):panel.eq(o).find(".panel__content").removeClass("panel__content--active")},scrolly=function(e){e.preventDefault();var n=this.hash,o=$(n);$("html, body").stop().animate({scrollTop:o.offset().top},300,"swing",function(){window.location.hash=n})},init=function(){burger.addEventListener("click",openMenu,!1),window.addEventListener("scroll",scrollFx,!1),window.addEventListener("load",scrollFx,!1),$('a[href^="#"]').on("click",scrolly)};doc.on("ready",init);